import {
  ArrowBackIosOutlined,
  ArrowForwardIosOutlined,
  DragHandle,
} from "@material-ui/icons";
import React from "react";
import "./list.scss";
import ListItem from "../ListItem/ListItem";
import { useRef } from "react";
const List = ({ moviedata }) => {
  console.log(moviedata);

  const listRef = useRef();

  const handleClick = (direction) => {
    let distance = listRef.current.getBoundingClientRect().x - 50;

    if (direction === "left") {
      listRef.current.style.transform = `translateX(${230 + distance}px)`;
    }

    if (direction === "right") {
      listRef.current.style.transform = `translateX(${-230 + distance}px)`;
    }
  };
  return (
    <div className="list">
      <span className="listTitle">Continue to watch</span>
      <div className="wrapper">
        <ArrowBackIosOutlined
          className="sliderArrow left"
          onClick={() => handleClick("left")}
        />
        <div className="container" ref={listRef}>
          {/* <ListItem index={0}/>
        <ListItem index={1}/>
        <ListItem index={2}/>
        <ListItem index={3}/>
        <ListItem index={4}/> */}

          {[
            {
              id: 1,
              name: "Vikings: Valhalla",
              genre: "Action/Drama",
              description:
                "Season 2 finds our heroes shortly after the tragic fall of Kattegat — an event that has shattered their dreams and altered their destinies. Suddenly finding themselves fugitives in Scandinavia, they are forced to test their ambitions and courage in worlds beyond the fjords of Kattegat.",
              rating: "3",
              trailer: "https://www.youtube.com/embed/Yi4YnwFT7Gk",
              poster:
                "https://m.media-amazon.com/images/M/MV5BMmZlYTQ3NjQtZmIxNC00MzA4LTg1NDgtMmEzODk0MzA4NjQxXkEyXkFqcGdeQXVyMTEyMjM2NDc2._V1_FMjpg_UX1000_.jpg",
            },
            {
              id: 2,
              name: "Vikings: Valhalla",
              genre: "Action/Drama",
              description:
                "Season 2 finds our heroes shortly after the tragic fall of Kattegat — an event that has shattered their dreams and altered their destinies. Suddenly finding themselves fugitives in Scandinavia, they are forced to test their ambitions and courage in worlds beyond the fjords of Kattegat.",
              rating: "3",
              trailer: "https://www.youtube.com/embed/Yi4YnwFT7Gk",
              poster:
                "https://m.media-amazon.com/images/M/MV5BMmZlYTQ3NjQtZmIxNC00MzA4LTg1NDgtMmEzODk0MzA4NjQxXkEyXkFqcGdeQXVyMTEyMjM2NDc2._V1_FMjpg_UX1000_.jpg",
            },
            {
              id: 3,
              name: "Vikings: Valhalla",
              genre: "Action/Drama",
              description:
                "Season 2 finds our heroes shortly after the tragic fall of Kattegat — an event that has shattered their dreams and altered their destinies. Suddenly finding themselves fugitives in Scandinavia, they are forced to test their ambitions and courage in worlds beyond the fjords of Kattegat.",
              rating: "3",
              trailer: "https://www.youtube.com/embed/Yi4YnwFT7Gk",
              poster:
                "https://m.media-amazon.com/images/M/MV5BMmZlYTQ3NjQtZmIxNC00MzA4LTg1NDgtMmEzODk0MzA4NjQxXkEyXkFqcGdeQXVyMTEyMjM2NDc2._V1_FMjpg_UX1000_.jpg",
            },
          ].map((movie) => (
            <ListItem key={movie.id} movie={movie} />
          ))}
        </div>
        <ArrowForwardIosOutlined
          className="sliderArrow right"
          onClick={() => handleClick("right")}
        />
      </div>
    </div>
  );
};

export default List;
